    public function testDependenciesAdded($config)
    {
        $zepto = new Sonic($config);

        // Check for request
        $this->assertArrayHasKey('request', $zepto->app);
        $this->assertInstanceOf(
            'Symfony\Component\HttpFoundation\Request',
