        // Check for response
        $this->assertArrayHasKey('response', $zepto->app);
        $this->assertInstanceOf(
            'Symfony\Component\HttpFoundation\Response',
            $zepto->app['response']
        );

        // Check for router
        $this->assertArrayHasKey('router', $zepto->app);
