    public function testLoadWithPluginThatDoesNotImplementInterface()
    {
        $this->loader = new PluginLoader(ROOT_DIR . 'tests/mocks/');
        $actual       = $this->loader->load('NoImplementInterfacePlugin.php');
    }

    /**
